public class a2040 {

	private String name;
	private int age;

	public a2040() {
		super();
		// TODO Auto-generated constructor stub
	}

	public a2040(String name, int age) {
		super();
		this.name = name;
		this.age = age;
	}

	public String getName() {
		return name;
	}

	public void setName(String name) {
		this.name = name;
	}

	public int getAge() {
		return age;
	}

	public void setAge(int age) {
		this.age = age;
	}

	@Override
	public String toString() {
		return "a2040 [name=" + name + ", age=" + age + "]";
	}

}
